import React from 'react'

const App = (props) => {
  const course = "Half Stack application development"
  const parts = [
    {
      name: "Fundamentals of React",
      exercises: 10
    },
    { 
      name: "Using props to pass data",
      exercises: 7
    },
    {
      name: "Start of a component",
      exercises: 14
    }
  ]
  return (
    <div>
      <Header course={course}/>
      <Content part={parts} />
      <Total exercises={parts} />
    </div>
  )
}

const Header = (props) => {
  console.log(props)
  return (
    <h1>{props.course}</h1>
  )
}

const Content = (props) => {
  console.log(props)
  return (
    <div>
      props.parts.forEach(item => {
        <>
          <Part part={item.name} exercises={item.exercises} />
        </>
      });
    </div>
  )
}

const Part = (props) => {
  console.log(props)
  return (
    <>
      <p>
        {props.part} {props.exercises}
      </p>
    </>
  )
}

const Total = (props) => {
  console.log(props)
  return (
    <>
      <p>Number of exercises {props.exercises1 + props.exercises2 + props.exercises3}</p>
    </>
  )
}

export default App;
