import React from 'react'
const App = () => {
  const course = {
    id: 1,
    name: "Half Stack application development",
    parts: [
      {
        name: "Fundamentals of React",
        exercises: 10,
        id: 1
      },
      { 
        name: "Using props to pass data",
        exercises: 7,
        id: 2
      },
      {
        name: "Start of a component",
        exercises: 14,
        id: 3
      }
    ]
  }
  return (
    <div>
      <Course course={course}/>
    </div>
  )
}

const Course = ({course}) => {
  return(
    <>
      <Header name={course.name} />
      {course.parts.map(part => <p key={part.name}>{part.name} {part.exercises}</p>)}
      <Total parts={course.parts}/>
    </>
  )
}

const Header = (name) => {
  return (
    <h1>{name.name}</h1>
  )
}

const Total = ({parts}) => {

  return (
    <>
      <p>Number of exercises {parts.reduce((s, p) => s + p.exercises)}</p>
    </>
  )
}

export default App;
